import React from "react";

function Note(props) {
  const [deteteNote, setDeleteNote] = useState("");
  function handelDelete() {
    setDeleteNote((prevValue, index) => {
      return prevValue.filter(index !== deteteNote);
    });
  }
  return (
    <div className="note">
      <h1>{props.title}</h1>
      <p>{props.content}</p>
      <button onClick={handleDelete}>DELETE</button>
    </div>
  );
}

export default Note;
